 */
async function sh(cmd) {
  return new Promise(function (resolve, reject) {
    const start = new Date();
    console.log(`[${format(start)}] Starting '${cmd}'...`);
    exec(cmd, (err, stdout, stderr) => {
      const end = new Date();
      const time = end.getTime() - start.getTime();
